from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """
    Convert a number to a string, using the given alphabet.
    The number represents a short uuid.
    The output has the most significant digit first.
    @param number: Int value
    @param alphabet: List with letters
    @param padding: Optional with int value
    @return: string value corresponded to int
    """
    if not isinstance(number, int):
        raise ValueError("Number must be an integer")

    if not isinstance(alphabet, list) or len(alphabet) < 2:
        raise ValueError("Alphabet must be a list with at least two characters")

    if padding is not None and not isinstance(padding, int):
        raise ValueError("Padding must be an integer or None")

    if number == 0:
        return alphabet[0] if padding is None or padding <= 1 else alphabet[0] * padding

    base = len(alphabet)
    result = []

    while number > 0:
        number, remainder = divmod(number, base)
        result.append(alphabet[remainder])

    result.reverse()

    final_result = ''.join(result)

    if padding is not None and len(final_result) < padding:
        final_result = alphabet[0] * (padding - len(final_result)) + final_result

    return final_result

