def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """
    Convert a number to a string, using the given alphabet.
    The number represents a short uuid.
    The output has the most significant digit first.
    @param number: Int value
    @param alphabet : List with letters
    @param padding : Optional with int value
    @return  string value corresponded to int
    """
    if not alphabet:
        raise ValueError("Alphabet must be provided and must contain at least one character.")
    if padding is not None and padding < 0:
        raise ValueError("Padding must be a non-negative integer.")
    if number < 0:
        raise ValueError("Number must be non-negative.")
    base = len(alphabet)
    if base <= 1:
        raise ValueError("Alphabet must contain at least two characters.")
    if not all(isinstance(char, str) and len(char) == 1 for char in alphabet):
        raise ValueError("All characters in the alphabet must be single characters.")
    if len(set(alphabet)) != len(alphabet):
        raise ValueError("Alphabet must contain unique characters.")

    if padding is not None:
        number = number * (padding // base + 1) + number // (base ** (padding - 1))

    result = ""
    while number > 0:
        number, remainder = divmod(number, base)
        result = alphabet[remainder] + result

    return result

