def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """
    Convert a number to a string, using the given alphabet.
    The number represents a short UUID.
    The output has the most significant digit first.

    @param number: int, the integer to convert
    @param alphabet: List[str], a list of characters to use for conversion
    @param padding: Optional[int], the number of characters to pad the output with (if shorter than this)
    @return: str, the string representation of the number based on the custom alphabet
    """
    if not isinstance(number, int):
        raise ValueError("Input number must be an integer.")

    if not isinstance(alphabet, list) or len(alphabet) < 2:
        raise ValueError("Alphabet must be a list with at least two characters.")

    if padding is not None and not isinstance(padding, int):
        raise ValueError("Padding must be an integer or None.")

    if number < 0:
        raise ValueError("Number must be non-negative.")

    if number == 0:
        return alphabet[0] * padding if padding is not None and padding >= 1 else ''

    result = []
    while number > 0:
        number, remainder = divmod(number, len(alphabet))
        result.append(alphabet[remainder])

    result_str = ''.join(reversed(result))

    if padding is not None and len(result_str) < padding:
        result_str = alphabet[0] * (padding - len(result_str)) + result_str

    return result_str

