from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """
    Convert a number to a string, using the given alphabet.
    The number represents a short uuid.
    The output has the most significant digit first.
    @param number: Int value
    @param alphabet : List with letters
    @param padding : Optional with int value
    @return  string value corresponded to int
    """
    if not alphabet:
        raise ValueError("Alphabet must be provided and not empty.")

    if padding is not None and padding < 0:
        raise ValueError("Padding must be a non-negative integer or None.")

    if number < 0:
        raise ValueError("Number must be a non-negative integer.")

    # Create a copy of the alphabet to avoid modifying the original list
    base_alphabet = alphabet[:]

    # Convert the number to the string representation
    if number == 0:
        return base_alphabet[0] if base_alphabet else ""

    result = []
    while number > 0:
        number, remainder = divmod(number, len(base_alphabet))
        result.append(base_alphabet[remainder])

    result_str = ''.join(result[::-1])

    # Apply padding if necessary
    if padding is not None:
        result_str = result_str.zfill(padding)

    return result_str

