import math

def is_non_prime(n: int) -> bool:
    """
    The function takes an integer n and returns True if n is a non-prime number, otherwise False.
    """
    if n <= 1:
        return True
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return True
    return False

