def subprocess_run_helper(func, *args, timeout, extra_env=None):
    """
    Helper function to run a given function in a subprocess with a timeout.

    Parameters:
    func (callable): The function to run.
    *args: Arguments to pass to the function.
    timeout (int): The timeout duration in seconds.
    extra_env (dict, optional): Additional environment variables to set for the subprocess.

    Returns:
    CompletedProcess: The result of the subprocess execution.
    """
    if extra_env is None:
        extra_env = {}

    def target():
        try:
            return func(*args)
        except Exception as e:
            return e

    with multiprocessing.Pool(processes=1, initializer=None, initargs=None, maxtasksperchild=None) as pool:
        result = pool.apply_async(target, ())
        try:
            return_value = result.get(timeout)
        except multiprocessing.TimeoutError:
            pool.terminate()
            pool.join()
            return subprocess.CompletedProcess(args=(), returncode=1, stdout=b'', stderr=b'TimeoutError')
        except Exception as e:
            pool.terminate()
            pool.join()
            return subprocess.CompletedProcess(args=(), returncode=1, stdout=b'', stderr=b'Exception: ' + str(e).encode())
        else:
            pool.terminate()
            pool.join()
            return subprocess.CompletedProcess(args=(), returncode=0, stdout=return_value.stdout, stderr=return_value.stderr)

