    """
    if extra_env is None:
        extra_env = {}

    def run_func():
        return func(*args)

    try:
        result = subprocess.run(
            [sys.executable, '-c', 'import run_func; run_func.main()'],
            timeout=timeout,
            env={**sys.env, **extra_env},
            capture_output=True,
            text=True
        )
    except subprocess.TimeoutExpired as e:
        raise TimeoutError("The function timed out") from e

    return result

