from typing import List
import math

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    s_sum = 0

    # Precompute the minimum distance to a pole for each point
    min_distance = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                min_distance[i][j] = 0
            else:
                if i > 0:
                    min_distance[i][j] = min(min_distance[i][j], min_distance[i - 1][j] + 1)
                if j > 0:
                    min_distance[i][j] = min(min_distance[i][j], min_distance[i][j - 1] + 1)

    for i in range(n, -1, -1):
        for j in range(m, -1, -1):
            if i < n:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i + 1][j] + 1)
            if j < m:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j + 1] + 1)

    # Calculate the sum of S(x, y) for all points in the grid
    for i in range(n + 1):
        for j in range(m + 1):
            s_sum += min_distance[i][j] ** 2

    return s_sum

# Read input from stdin
n, m = map(int, input().split())
grid = [list(map(int, input().split())) for _ in range(n + 1)]

# Calculate the result
result = calculate_sum_of_s(grid)

# Print the result
print(result)

