from typing import List, Tuple, Dict
import sys
import heapq

# Assuming a TreeNode class is defined somewhere in the project
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List[TreeNode] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

# Assuming a Graph class is defined somewhere in the project
class Graph:
    def __init__(self):
        self.nodes: Dict[int, TreeNode] = {}

    def add_node(self, room_id: int, illusion_rate: int):
        if room_id not in self.nodes:
            self.nodes[room_id] = TreeNode(room_id, illusion_rate)

    def get_node(self, room_id: int) -> TreeNode:
        return self.nodes.get(room_id)

    def update_illusion_rate(self, room_id: int, new_rate: int):
        if room_id in self.nodes:
            self.nodes[room_id].illusion_rate = new_rate

# Function to calculate the minimum energy required to travel between two rooms
def calculate_minimum_energy(start_room: int, end_room: int) -> int:
    pq = [(0, start_room)]
    visited = set()
    energy_map = {start_room: 0}

    while pq:
        current_energy, current_room = heapq.heappop(pq)
        if current_room == end_room:
            return current_energy
        if current_room in visited:
            continue
        visited.add(current_room)

        for child in graph.get_node(current_room).children:
            child_room = child.room_id
            if child_room in visited:
                continue
            new_energy = max(abs(graph.get_node(current_room).illusion_rate - graph.get_node(child_room).illusion_rate),
                             abs(graph.get_node(child_room).illusion_rate - graph.get_node(current_room).illusion_rate))
            if child_room not in energy_map or new_energy < energy_map[child_room]:
                energy_map[child_room] = new_energy
                heapq.heappush(pq, (new_energy, child_room))

    return -1  # If no path is found, which should not happen in a tree

# Function to update the illusion rate of a room
def update_illusion_rate(room_id: int, new_rate: int) -> None:
    graph.update_illusion_rate(room_id, new_rate)

# Main function to handle input and output
def main():
    n, q = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))

    # Initialize the graph
    global graph
    graph = Graph()

    # Build the tree structure
    for room_id, illusion_rate in enumerate(a, start=1):
        graph.add_node(room_id, illusion_rate)

    for _ in range(n - 1):
        s, t = map(int, sys.stdin.readline().split())
        graph.get_node(s).add_child(graph.get_node(t))
        graph.get_node(t).add_child(graph.get_node(s))

    # Process queries
    for _ in range(q):
        query = list(map(int, sys.stdin.readline().split()))
        if query[0] == 1:
            update_illusion_rate(query[1], query[2])
        elif query[0] == 2:
            result = calculate_minimum_energy(query[1], query[2])
            print(result)

if __name__ == "__main__":
    main()

