def process_queries(q: int, queries: List[List[int]]) -> List[int]:
    array = []
    count_map = {}

    for query in queries:
        if query[0] == 1:
            x = query[1]
            array.append(x)
            if x in count_map:
                count_map[x] += 1
            else:
                count_map[x] = 1
        elif query[0] == 2:
            x, y = query[1], query[2]
            if y in count_map:
                count_map[y] += count_map.get(x, 0)
            else:
                count_map[y] = count_map.get(x, 0)
            count_map[x] = 0
            array = [y if elem == x else elem for elem in array]

    return array

