import sys
from typing import List, Tuple
import heapq

# Assuming a simple graph representation using adjacency list
Graph = List[List[Tuple[int, int]]]

# Function to update the illusion rate of a room
def update_illusion_rate(room_id: int, new_rate: int) -> None:
    illusion_rates[room_id - 1] = new_rate

# Function to query the minimum energy required to travel between two rooms
def query_minimum_energy(start_room: int, end_room: int) -> int:
    # Implement Dijkstra's algorithm to find the minimum energy
    def dijkstra(start: int, end: int) -> int:
        pq = [(0, start)]
        dist = [float('inf')] * n
        dist[start - 1] = 0
        while pq:
            current_energy, node = heapq.heappop(pq)
            if node == end:
                return current_energy
            if current_energy > dist[node - 1]:
                continue
            for neighbor, weight in graph[node - 1]:
                new_energy = current_energy + weight
                if new_energy < dist[neighbor - 1]:
                    dist[neighbor - 1] = new_energy
                    heapq.heappush(pq, (new_energy, neighbor))
        return float('inf')

    # Calculate the energy considering the illusion rates
    def calculate_energy(x: int, y: int) -> int:
        return max(abs(illusion_rates[x - 1] + illusion_rates[y - 1]), abs(illusion_rates[x - 1] - illusion_rates[y - 1]))

    # Precompute energies for faster querying
    energies = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            energies[i][j] = calculate_energy(i, j)

    return dijkstra(start_room, end_room)

# Read input
n, q = map(int, sys.stdin.readline().split())
illusion_rates = list(map(int, sys.stdin.readline().split()))

# Initialize graph
graph: Graph = [[] for _ in range(n)]

# Read tunnels
for _ in range(n - 1):
    s, t = map(int, sys.stdin.readline().split())
    energy = calculate_energy(s - 1, t - 1)
    graph[s - 1].append((t - 1, energy))
    graph[t - 1].append((s - 1, energy))

# Process queries
for _ in range(q):
    query_type, *args = map(int, sys.stdin.readline().split())
    if query_type == 1:
        u, c = args
        update_illusion_rate(u, c)
        # Update the graph with new energy values
        for neighbor, _ in graph[u - 1]:
            new_energy = calculate_energy(u - 1, neighbor)
            for i, (node, weight) in enumerate(graph[neighbor]):
                if node == u - 1:
                    graph[neighbor][i] = (u - 1, new_energy)
                    break
    elif query_type == 2:
        u, v = args
        result = query_minimum_energy(u - 1, v - 1)
        print(result)

