from typing import List, Tuple
import sys

# Assuming a tree is represented as an adjacency list
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List[TreeNode] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

class Tree:
    def __init__(self, n: int, illusion_rates: List[int]):
        self.nodes = {i + 1: TreeNode(i + 1, illusion_rates[i]) for i in range(n)}
        self.root = self.nodes[1]  # Assuming the first room is the root

    def add_edge(self, s: int, t: int):
        self.nodes[s].add_child(self.nodes[t])
        self.nodes[t].add_child(self.nodes[s])

    def update_illusion_rate(self, room_id: int, new_rate: int):
        node = self.nodes.get(room_id)
        if node:
            node.illusion_rate = new_rate

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        start_node = self.nodes.get(start_room)
        end_node = self.nodes.get(end_room)
        if not start_node or not end_node:
            return -1  # Error: room not found

        # Using DFS to find the minimum energy
        stack = [(start_node, end_node, 0)]
        visited = set()

        while stack:
            current, target, energy = stack.pop()
            if current == target:
                return energy + max(abs(start_node.illusion_rate + end_node.illusion_rate), abs(start_node.illusion_rate - end_node.illusion_rate))
            if current in visited:
                continue
            visited.add(current)
            for child in current.children:
                if child not in visited:
                    new_energy = energy + max(abs(current.illusion_rate + child.illusion_rate), abs(current.illusion_rate - child.illusion_rate))
                    stack.append((child, target, new_energy))
        return -1  # Error: no path found

