import sys
from typing import List, Tuple, Dict

# Define the graph as an adjacency list
Graph = Dict[int, List[int]]

# Define the illusion rates as a dictionary
IllusionRates = Dict[int, int]

# Define the energy matrix as a dictionary of dictionaries
EnergyMatrix = Dict[int, Dict[int, int]]

def read_ints() -> List[int]:
    return list(map(int, sys.stdin.readline().strip().split()))

def build_graph(n: int, edges: List[Tuple[int, int]]) -> Graph:
    graph: Graph = {i: [] for i in range(1, n + 1)}
    for s, t in edges:
        graph[s].append(t)
        graph[t].append(s)
    return graph

def build_illusion_rates(rates: List[int]) -> IllusionRates:
    return {i + 1: rates[i] for i in range(len(rates))}

def build_energy_matrix(rates: IllusionRates, graph: Graph) -> EnergyMatrix:
    energy_matrix: EnergyMatrix = {i: {} for i in rates.keys()}
    for u in rates.keys():
        for v in rates.keys():
            if u != v:
                energy_matrix[u][v] = max(abs(rates[u] + rates[v]), abs(rates[u] - rates[v]))
    return energy_matrix

def update_illusion_rate(room_id: int, new_rate: int, rates: IllusionRates) -> None:
    rates[room_id] = new_rate

def calculate_minimum_energy(start_room: int, end_room: int, energy_matrix: EnergyMatrix) -> int:
    return energy_matrix[start_room][end_room]

def main():
    n, q = read_ints()
    illusion_rates = build_illusion_rates(read_ints())
    edges = [tuple(read_ints()) for _ in range(n - 1)]
    graph = build_graph(n, edges)
    energy_matrix = build_energy_matrix(illusion_rates, graph)

    for _ in range(q):
        query = read_ints()
        if query[0] == 1:
            update_illusion_rate(query[1], query[2], illusion_rates)
            energy_matrix = build_energy_matrix(illusion_rates, graph)  # Update energy matrix after update
        elif query[0] == 2:
            result = calculate_minimum_energy(query[1], query[2], energy_matrix)
            print(result)

if __name__ == "__main__":
    main()

