from typing import List, Tuple
import sys

# Define the tree structure and its operations
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List[TreeNode] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

class Tree:
    def __init__(self, n: int, room_illusions: List[int]):
        self.nodes = [TreeNode(i + 1, room_illusions[i]) for i in range(n)]
        self.root = self.nodes[0]

    def add_edge(self, s: int, t: int):
        self.nodes[s - 1].add_child(self.nodes[t - 1])
        self.nodes[t - 1].add_child(self.nodes[s - 1])

    def update_illusion_rate(self, room_id: int, new_rate: int):
        self.nodes[room_id - 1].illusion_rate = new_rate

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        def dfs(node: TreeNode, target: int, parent: int, energy: int) -> Tuple[bool, int]:
            if node.room_id == target:
                return True, energy
            for child in node.children:
                if child.room_id != parent:
                    found, energy_child = dfs(child, target, node.room_id, max(energy, abs(node.illusion_rate - child.illusion_rate)))
                    if found:
                        return found, energy_child
            return False, 0

        found, energy = dfs(self.nodes[start_room - 1], end_room, -1, 0)
        return energy

# Read input and process queries
def main():
    input_lines = sys.stdin.readlines()
    n, q = map(int, input_lines[0].split())
    room_illusions = list(map(int, input_lines[1].split()))
    edges = [list(map(int, line.split())) for line in input_lines[2:2 + n - 1]]
    queries = [list(map(int, line.split())) for line in input_lines[2 + n - 1:]]

    tree = Tree(n, room_illusions)
    for s, t in edges:
        tree.add_edge(s, t)

    for query in queries:
        query_type = query[0]
        if query_type == 1:
            room_id, new_rate = query[1], query[2]
            tree.update_illusion_rate(room_id, new_rate)
        elif query_type == 2:
            start_room, end_room = query[1], query[2]
            min_energy = tree.calculate_minimum_energy(start_room, end_room)
            print(min_energy)

if __name__ == "__main__":
    main()

