from typing import List, Tuple
import sys

# Assuming a simple tree structure using adjacency list
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List['TreeNode'] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

class TreeStructure:
    def __init__(self, n: int, room_illusions: List[int], edges: List[Tuple[int, int]]):
        self.rooms = [TreeNode(i + 1, room_illusions[i]) for i in range(n)]
        self.build_tree(edges)

    def build_tree(self, edges: List[Tuple[int, int]]):
        for s, t in edges:
            self.rooms[s - 1].add_child(self.rooms[t - 1])
            self.rooms[t - 1].add_child(self.rooms[s - 1])

    def update_illusion_rate(self, room_id: int, new_rate: int):
        room = self.find_room(room_id)
        if room:
            room.illusion_rate = new_rate

    def find_room(self, room_id: int) -> TreeNode:
        for room in self.rooms:
            if room.room_id == room_id:
                return room
        return None

class DFSAlgorithm:
    def __init__(self, tree: TreeStructure):
        self.tree = tree

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        start_node = self.tree.find_room(start_room)
        end_node = self.tree.find_room(end_room)
        if not start_node or not end_node:
            return -1  # Error: room not found

        # Using a more efficient algorithm like Dijkstra's for shortest path
        import heapq
        pq = [(0, start_node)]
        distances = {room: float('inf') for room in self.tree.rooms}
        distances[start_node] = 0
        while pq:
            current_distance, current_node = heapq.heappop(pq)
            if current_node == end_node:
                return current_distance + abs(start_node.illusion_rate + end_node.illusion_rate)
            for child in current_node.children:
                energy = abs(current_node.illusion_rate + child.illusion_rate)
                new_distance = current_distance + energy
                if new_distance < distances[child]:
                    distances[child] = new_distance
                    heapq.heappush(pq, (new_distance, child))
        return -1  # Error: no path found

# Main function to handle input and output
def main():
    n, q = map(int, sys.stdin.readline().split())
    room_illusions = list(map(int, sys.stdin.readline().split()))
    edges = [tuple(map(int, sys.stdin.readline().split())) for _ in range(n - 1)]
    queries = [list(map(int, sys.stdin.readline().split())) for _ in range(q)]

    tree_structure = TreeStructure(n, room_illusions, edges)
    dfs_algorithm = DFSAlgorithm(tree_structure)

    for query in queries:
        if query[0] == 1:
            tree_structure.update_illusion_rate(query[1], query[2])
        elif query[0] == 2:
            result_energy = dfs_algorithm.calculate_minimum_energy(query[1], query[2])
            print(result_energy)

if __name__ == "__main__":
    main()

