from typing import List

def func(queries: List[str]) -> List[int]:
    array = []
    replacement_map = {}

    for query in queries:
        parts = query.split()
        if parts[0] == '1':
            # Add the element x to the end of the array
            x = int(parts[1])
            array.append(x)
        elif parts[0] == '2':
            # Replace all occurrences of x in the array with y
            x = int(parts[1])
            y = int(parts[2])
            if x not in replacement_map:
                replacement_map[x] = y
            else:
                y = replacement_map[x]
            array = [y if elem == x else elem for elem in array]

    return array

