from typing import List

def calculate_pairs_to_remove(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    mean = total_sum / n

    frequency = {}
    for num in arr:
        frequency[num] = frequency.get(num, 0) + 1

    pairs_to_remove = 0

    for i in range(n):
        for j in range(i + 1, n):
            new_sum = total_sum - arr[i] - arr[j]
            new_mean = new_sum / (n - 2)
            if new_mean == mean:
                pairs_to_remove += frequency.get(arr[i], 0) * frequency.get(arr[j], 0)

    return pairs_to_remove

