from typing import List
import sys

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid)
    m = len(grid[0])
    total_sum = 0

    # Precompute the minimum distance to the nearest pole for each cell
    min_distance = [[float('inf')] * m for _ in range(n)]
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 1:
                min_distance[i][j] = 0
            elif i > 0:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i - 1][j] + 1)
            if j > 0:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j - 1] + 1)

    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            if i < n - 1:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i + 1][j] + 1)
            if j < m - 1:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j + 1] + 1)

    # Calculate the sum of S(x, y) for each cell
    for i in range(n):
        for j in range(m):
            total_sum += min_distance[i][j] ** 2

    return total_sum

