from typing import List, Tuple, Dict
import sys

# Define the Tree structure
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List[TreeNode] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

# Global variables to store the tree and illusion rates
tree: Dict[int, TreeNode] = {}

# Function to update the illusion rate of a room
def update_illusion_rate(room_id: int, new_rate: int) -> None:
    tree[room_id].illusion_rate = new_rate

# Function to calculate the minimum energy required to travel between two rooms
def calculate_minimum_energy(start_room: int, end_room: int) -> int:
    # Use DFS to find the minimum energy
    stack = [(start_room, -sys.maxsize)]
    visited = set()

    while stack:
        current_room, max_illusion_rate = stack.pop()
        if current_room == end_room:
            return max_illusion_rate
        if current_room not in visited:
            visited.add(current_room)
            current_node = tree[current_room]
            max_illusion_rate = max(max_illusion_rate, abs(current_node.illusion_rate))
            for child in current_node.children:
                stack.append((child.room_id, max_illusion_rate))

    return -1  # This should never be reached if the graph is guaranteed to be connected

# Initialize the tree
def initialize_tree(n: int, room_illusions: List[int], tunnels: List[Tuple[int, int]]) -> None:
    global tree
    tree = {}
    for i in range(1, n + 1):
        tree[i] = TreeNode(i, room_illusions[i - 1])
    for s, t in tunnels:
        tree[s].add_child(tree[t])
        tree[t].add_child(tree[s])

# Read input and process queries
def main():
    n, q = map(int, input().split())
    room_illusions = list(map(int, input().split()))
    tunnels = [tuple(map(int, input().split())) for _ in range(n - 1)]

    initialize_tree(n, room_illusions, tunnels)

    for _ in range(q):
        query = list(map(int, input().split()))
        if query[0] == 1:
            update_illusion_rate(query[1], query[2])
        elif query[0] == 2:
            result = calculate_minimum_energy(query[1], query[2])
            print(result)

if __name__ == "__main__":
    main()

