def calculate_sum_of_squares(n: int, m: int, grid: List[List[int]]) -> int:
    def distance_squared(x1: int, y1: int, x2: int, y2: int) -> int:
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    sum_of_squares = 0

    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                nearest_pole_distance_squared = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            nearest_pole_distance_squared = min(nearest_pole_distance_squared, distance_squared(x, y, i, j))
                sum_of_squares += nearest_pole_distance_squared

    return sum_of_squares

