def find_max_f(sequence: list[int]) -> int:
    def f(seq: list[int]) -> int:
        return sum(1 for i, x in enumerate(seq, 1) if i == x)

    max_f = f(sequence)
    current_sequence = sequence[:]

    while True:
        indices_to_remove = [i for i, x in enumerate(current_sequence, 1) if i != x]
        if not indices_to_remove:
            break
        for idx in sorted(indices_to_remove, reverse=True):
            current_sequence.pop(idx - 1)
        max_f = max(max_f, f(current_sequence))

    return max_f

# Interface using input() and print()
