from typing import List

def is_string_square(test_cases: List[str]) -> List[str]:
    results = []
    for test_case in test_cases:
        length = len(test_case)
        if length % 2 == 0:
            first_half = test_case[:length//2]
            second_half = test_case[length//2:]
            if first_half == second_half:
                results.append('YES')
            else:
                results.append('NO')
        else:
            results.append('NO')
    return results

# Interface using input() and print()
test_cases_count = int(input())
test_cases = [input() for _ in range(test_cases_count)]
results = is_string_square(test_cases)
for result in results:
    print(result)

