from typing import List

def calculate_sum_of_squares(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1

    # Create a prefix sum array for the number of poles
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Fill the prefix sum array
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = grid[i][j] + prefix_sum[i-1][j] + prefix_sum[i][j-1] - prefix_sum[i-1][j-1]

    def count_poles(x1, y1, x2, y2):
        return prefix_sum[x2][y2] - prefix_sum[x1-1][y2] - prefix_sum[x2][y1-1] + prefix_sum[x1-1][y1-1]

    def sum_of_squares(x, y):
        total_poles = count_poles(x, y, x, y)
        if total_poles == 1:
            return 0
        return total_poles * (x**2 + y**2) - 2 * (x * count_poles(1, 1, x, y) + y * count_poles(1, 1, x, y))

    result = 0
    for x in range(1, n + 1):
        for y in range(1, m + 1):
            result += sum_of_squares(x, y)

    return result

