from typing import List, Tuple

def calculate_max_steps(n: int, m: int, board: List[List[str]]) -> Tuple[int, int, int]:
    def move(r, c, direction):
        if direction == 'L':
            return r, max(c - 1, 0)
        elif direction == 'R':
            return r, min(c + 1, m - 1)
        elif direction == 'D':
            return min(r + 1, n - 1), c
        elif direction == 'U':
            return max(r - 1, 0), c

    def simulate(start_r, start_c):
        visited = [[False] * m for _ in range(n)]
        steps = 0
        r, c = start_r, start_c
        while True:
            if visited[r][c]:
                return steps
            visited[r][c] = True
            r, c = move(r, c, board[r][c])
            steps += 1

    max_steps = -1
    best_start = (0, 0, 0)

    for r in range(n):
        for c in range(m):
            steps = simulate(r, c)
            if steps > max_steps:
                max_steps = steps
                best_start = (r + 1, c + 1, steps)

    return best_start

