def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    s_sum = 0

    # Helper function to calculate the squared distance between two points
    def distance_squared(x1, y1, x2, y2):
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    # Precompute the minimum distance to the nearest pole for each point
    min_dist_grid = [[0] * (m + 1) for _ in range(n + 1)]
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                min_dist_grid[x][y] = 0
            else:
                min_dist_grid[x][y] = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            dist = distance_squared(x, y, i, j)
                            if dist < min_dist_grid[x][y]:
                                min_dist_grid[x][y] = dist

    # Sum up all S(x, y) values
    for x in range(n + 1):
        for y in range(m + 1):
            s_sum += min_dist_grid[x][y]

    return s_sum

