from typing import List

def calculate_sum_of_squares(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1

    # Create a prefix sum array for the number of poles
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Calculate the prefix sum for the number of poles
    for i in range(n + 1):
        for j in range(m + 1):
            prefix_sum[i][j] = grid[i][j]
            if i > 0:
                prefix_sum[i][j] += prefix_sum[i - 1][j]
            if j > 0:
                prefix_sum[i][j] += prefix_sum[i][j - 1]
            if i > 0 and j > 0:
                prefix_sum[i][j] -= prefix_sum[i - 1][j - 1]

    def count_poles(x1, y1, x2, y2):
        # Count the number of poles in the subgrid
        count = prefix_sum[x2][y2]
        if x1 > 0:
            count -= prefix_sum[x1 - 1][y2]
        if y1 > 0:
            count -= prefix_sum[x2][y1 - 1]
        if x1 > 0 and y1 > 0:
            count += prefix_sum[x1 - 1][y1 - 1]
        return count

    result = 0
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                result += x * x + y * y
            else:
                min_distance_sq = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            distance_sq = (x - i) ** 2 + (y - j) ** 2
                            min_distance_sq = min(min_distance_sq, distance_sq)
                result += min_distance_sq

    return result

