import numpy as np

def calculate_sum_of_squares(grid: np.ndarray) -> int:
    n, m = grid.shape
    total_sum = 0

    # Precompute the minimum distance to a pole for each point
    min_distance_grid = np.full((n, m), np.inf)
    for i in range(n):
        for j in range(m):
            if grid[i, j] == 1:
                min_distance_grid[i, j] = 0
            else:
                if i > 0:
                    min_distance_grid[i, j] = min(min_distance_grid[i, j], min_distance_grid[i - 1, j] + 1)
                if j > 0:
                    min_distance_grid[i, j] = min(min_distance_grid[i, j], min_distance_grid[i, j - 1] + 1)

    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            if i < n - 1:
                min_distance_grid[i, j] = min(min_distance_grid[i, j], min_distance_grid[i + 1, j] + 1)
            if j < m - 1:
                min_distance_grid[i, j] = min(min_distance_grid[i, j], min_distance_grid[i, j + 1] + 1)

    # Calculate the sum of squares of distances
    for i in range(n):
        for j in range(m):
            total_sum += min_distance_grid[i, j] ** 2

    return total_sum

