from typing import List, Tuple

def simulate_robot(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def move(r: int, c: int, direction: str) -> Tuple[int, int]:
        if direction == 'L':
            return (r, max(c - 1, 0))
        elif direction == 'R':
            return (r, min(c + 1, m - 1))
        elif direction == 'D':
            return (max(r - 1, 0), c)
        elif direction == 'U':
            return (min(r + 1, n - 1), c)
        else:
            raise ValueError("Invalid direction")

    max_steps = 0
    start_cell = (0, 0)

    for r in range(n):
        for c in range(m):
            visited = set()
            steps = 0
            cr, cc = r, c
            while (cr, cc) not in visited:
                visited.add((cr, cc))
                direction = board[cr][cc]
                cr, cc = move(cr, cc, direction)
                steps += 1
                if cr < 0 or cr >= n or cc < 0 or cc >= m:
                    break

            if steps > max_steps:
                max_steps = steps
                start_cell = (r + 1, c + 1)

    return start_cell + (max_steps,)

