from typing import List, Tuple

def calculate_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def move_robot(start_row: int, start_col: int) -> int:
        visited = [[False] * m for _ in range(n)]
        steps = 0
        row, col = start_row, start_col

        while True:
            if visited[row][col]:
                return -1  # Robot breaks if it revisits a cell
            visited[row][col] = True
            direction = board[row][col]
            if direction == 'L':
                col -= 1
            elif direction == 'R':
                col += 1
            elif direction == 'D':
                row += 1
            elif direction == 'U':
                row -= 1

            if row < 0 or row >= n or col < 0 or col >= m:
                return steps  # Robot falls off the board

            steps += 1

    max_steps = 0
    best_position = (0, 0)

    for row in range(n):
        for col in range(m):
            steps = move_robot(row, col)
            if steps > max_steps:
                max_steps = steps
                best_position = (row + 1, col + 1)  # Convert to 1-based index

    return best_position + (max_steps,)

