import numpy as np

def calculate_sum_of_s(grid: np.ndarray) -> int:
    n, m = grid.shape
    sum_of_s = 0

    # Precompute distances from each point to the nearest pole
    distances = np.full((n, m), np.inf)
    for i in range(n):
        for j in range(m):
            if grid[i, j] == 1:
                distances[i, j] = 0
            elif i > 0:
                distances[i, j] = min(distances[i, j], distances[i - 1, j] + 1)
            if j > 0:
                distances[i, j] = min(distances[i, j], distances[i, j - 1] + 1)

    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            if i < n - 1:
                distances[i, j] = min(distances[i, j], distances[i + 1, j] + 1)
            if j < m - 1:
                distances[i, j] = min(distances[i, j], distances[i, j + 1] + 1)

    # Calculate the sum of squares of distances
    for x in range(n):
        for y in range(m):
            sum_of_s += distances[x, y] ** 2

    return sum_of_s

