from typing import List, Tuple

def calculate_max_steps(n: int, m: int, board: List[List[str]]) -> Tuple[int, int, int]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m

    def move(x: int, y: int, direction: str) -> Tuple[int, int]:
        if direction == 'L':
            return (x, y - 1)
        elif direction == 'R':
            return (x, y + 1)
        elif direction == 'D':
            return (x + 1, y)
        elif direction == 'U':
            return (x - 1, y)
        else:
            raise ValueError("Invalid direction")

    max_steps = 0
    best_start = (0, 0, 0)

    for start_x in range(n):
        for start_y in range(m):
            visited = [[False] * m for _ in range(n)]
            steps = 0
            x, y = start_x, start_y

            while is_valid(x, y) and not visited[x][y]:
                visited[x][y] = True
                direction = board[x][y]
                x, y = move(x, y, direction)
                steps += 1

            if steps > max_steps:
                max_steps = steps
                best_start = (start_x + 1, start_y + 1, steps)

    return best_start

