def process_queries(q: int) -> list:
    array = []
    occurrences = {}

    for _ in range(q):
        query = input().split()

        if query[0] == '1':
            x = int(query[1])
            array.append(x)
            if x in occurrences:
                occurrences[x] += 1
            else:
                occurrences[x] = 1
        elif query[0] == '2':
            x = int(query[1])
            y = int(query[2])
            if x in occurrences:
                count_x = occurrences[x]
                array = [y if elem == x else elem for elem in array]
                if y in occurrences:
                    occurrences[y] += count_x
                else:
                    occurrences[y] = count_x
                del occurrences[x]

    return array

