def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    max_dist = n + m

    # Create a 2D prefix sum array to store the minimum distance to a pole
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Fill the prefix sum array with the minimum distance to a pole
    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                prefix_sum[i][j] = 0
            else:
                if i > 0 and j > 0:
                    prefix_sum[i][j] = min(prefix_sum[i-1][j], prefix_sum[i][j-1]) + 1
                elif i > 0:
                    prefix_sum[i][j] = prefix_sum[i-1][j] + 1
                elif j > 0:
                    prefix_sum[i][j] = prefix_sum[i][j-1] + 1

    # Calculate the sum of S(x, y) for each point (x, y) in the grid
    result = 0
    for i in range(n + 1):
        for j in range(m + 1):
            result += prefix_sum[i][j] ** 2

    return result

