def calculate_sum_of_distances(n: int, m: int, grid: List[List[int]]) -> int:
    # Precompute the prefix sums for the distances
    prefix_sums = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if grid[i][j] == 1:
                prefix_sums[i][j] = 1

    # Compute prefix sums of distances
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sums[i][j] += prefix_sums[i - 1][j] + prefix_sums[i][j - 1] - prefix_sums[i - 1][j - 1]

    def sum_of_distances(x1, y1, x2, y2):
        total_poles = (x2 - x1 + 1) * (y2 - y1 + 1)
        total_distance_sum = 0

        for i in range(x1, x2 + 1):
            for j in range(y1, y2 + 1):
                if grid[i][j] == 1:
                    distance_sum = 0
                    distance_sum += (i - x1) * (i - x1) + (j - y1) * (j - y1)
                    distance_sum += (i - x2) * (i - x2) + (j - y2) * (j - y2)
                    distance_sum += (i - x1) * (i - x1) + (j - y2) * (j - y2)
                    distance_sum += (i - x2) * (i - x2) + (j - y1) * (j - y1)
                    total_distance_sum += distance_sum

        return total_distance_sum

    result = 0
    for i in range(n + 1):
        for j in range(m + 1):
            result += sum_of_distances(0, 0, i, j)

    return result

