from typing import List, Tuple
import sys

# Assuming a tree is represented as an adjacency list
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List[TreeNode] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

def update_illusion_rate(room_id: int, new_rate: int) -> None:
    # This function will update the illusion rate of the room with the given room_id
    # We need to traverse the tree to find the node with the given room_id and update its illusion rate
    def dfs_update(node: TreeNode, room_id: int, new_rate: int):
        if node.room_id == room_id:
            node.illusion_rate = new_rate
            return
        for child in node.children:
            dfs_update(child, room_id, new_rate)

    dfs_update(root, room_id, new_rate)

def calculate_minimum_energy(start_room: int, end_room: int) -> int:
    # This function will calculate the minimum energy required to travel between two rooms
    # We need to use a graph traversal algorithm like DFS or BFS to find the shortest path
    # and then calculate the energy based on the illusion rates
    def dfs_energy(node: TreeNode, target: int, current_energy: int, min_energy: int) -> int:
        if node.room_id == target:
            return min(min_energy, current_energy)
        for child in node.children:
            min_energy = min(min_energy, dfs_energy(child, target, max(current_energy, abs(node.illusion_rate - child.illusion_rate)), min_energy))
        return min_energy

    return dfs_energy(root, end_room, 0, sys.maxsize)

# Initialize the tree
def initialize_tree(n: int, a: List[int], edges: List[Tuple[int, int]]) -> TreeNode:
    nodes = [TreeNode(i + 1, a[i]) for i in range(n)]
    for s, t in edges:
        nodes[s - 1].add_child(nodes[t - 1])
        nodes[t - 1].add_child(nodes[s - 1])
    return nodes[0]  # Return the root node

