from typing import List

def find_max_f(sequence: List[int]) -> int:
    n = len(sequence)
    f_values = [0] * n

    # Initialize f_values with the number of indices where a_i = i
    for i in range(n):
        if sequence[i] == i + 1:
            f_values[i] = 1

    # Calculate the prefix sum of f_values to get the cumulative f(a)
    for i in range(1, n):
        f_values[i] += f_values[i - 1]

    # Find the maximum f(a) by considering removing each element
    max_f = f_values[-1]
    for i in range(n):
        if sequence[i] != i + 1:
            # Calculate f(a) after removing the i-th element
            new_sequence = sequence[:i] + sequence[i+1:]
            new_f_values = [0] * (n - 1)
            for j in range(n - 1):
                if new_sequence[j] == j + 1:
                    new_f_values[j] = 1
            for j in range(1, n - 1):
                new_f_values[j] += new_f_values[j - 1]
            max_f = max(max_f, new_f_values[-1])

    return max_f

