def find_max_f(sequence: list[int]) -> int:
    def f(seq: list[int]) -> int:
        return sum(1 for i, val in enumerate(seq, start=1) if i == val)

    max_f = f(sequence)
    current_sequence = sequence[:]

    while True:
        to_remove = []
        for i, val in enumerate(current_sequence, start=1):
            if i != val:
                to_remove.append(val)

        if not to_remove:
            break

        for val in to_remove:
            current_sequence.remove(val)

        current_f = f(current_sequence)
        if current_f > max_f:
            max_f = current_f

    return max_f

