from typing import List, Tuple
from collections import deque

def find_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    # Directions for moving in the board
    directions = {
        'L': (0, -1),
        'R': (0, 1),
        'D': (1, 0),
        'U': (-1, 0)
    }

    def bfs(start_row: int, start_col: int) -> int:
        visited = [[False] * m for _ in range(n)]
        queue = deque([(start_row, start_col, 0)])  # (row, col, steps)
        visited[start_row][start_col] = True
        max_steps = 0

        while queue:
            row, col, steps = queue.popleft()
            max_steps = max(max_steps, steps)

            for direction, (dr, dc) in directions.items():
                new_row, new_col = row + dr, col + dc
                if 0 <= new_row < n and 0 <= new_col < m and not visited[new_row][new_col]:
                    if board[new_row][new_col] == direction:
                        visited[new_row][new_col] = True
                        queue.append((new_row, new_col, steps + 1))

        return max_steps

    max_steps = 0
    best_start = (0, 0, 0)

    for row in range(n):
        for col in range(m):
            steps = bfs(row, col)
            if steps > max_steps:
                max_steps = steps
                best_start = (row + 1, col + 1, steps)  # Convert to 1-based index

    return best_start

