def compare_numbers(x1: int, p1: int, x2: int, p2: int) -> str:
    # Determine the number of digits after the number for both x1 and x2
    len1 = len(str(x1)) + p1
    len2 = len(str(x2)) + p2

    # Compare the lengths of the numbers
    if len1 < len2:
        return '<'
    elif len1 > len2:
        return '>'
    else:
        # If the lengths are equal, compare the numbers without the appended zeros
        zeros1 = 10 ** p1
        zeros2 = 10 ** p2
        adjusted_x1 = x1 * zeros1
        adjusted_x2 = x2 * zeros2

        if adjusted_x1 < adjusted_x2:
            return '<'
        elif adjusted_x1 > adjusted_x2:
            return '>'
        else:
            return '='

