def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid)
    m = len(grid[0])
    sum_of_s = 0

    # Pre-compute the minimum distance to the nearest pole for each point
    min_distance = [[0] * (m + 1) for _ in range(n + 1)]

    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                min_distance[x][y] = 0
            else:
                min_distance[x][y] = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            distance = (x - i) ** 2 + (y - j) ** 2
                            if distance < min_distance[x][y]:
                                min_distance[x][y] = distance

    # Calculate the sum of S(x, y) for each point
    for x in range(n + 1):
        for y in range(m + 1):
            sum_of_s += min_distance[x][y]

    return sum_of_s

