from typing import List

def count_pairs(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    mean = total_sum / n

    if mean * 2 == total_sum:
        return n * (n - 1) // 2

    count_diff = {}
    for i in range(n):
        for j in range(i + 1, n):
            new_sum = total_sum - arr[i] - arr[j]
            new_mean = new_sum / (n - 2)
            if new_mean == mean:
                diff = (arr[i], arr[j]) if arr[i] < arr[j] else (arr[j], arr[i])
                count_diff[diff] = count_diff.get(diff, 0) + 1

    return sum(count_diff.values())

# Interface using input() and print()
t = int(input())
for _ in range(t):
    n = int(input())
    arr = list(map(int, input().split()))
    result = count_pairs(n, arr)
    print(result)
