    def distance_squared(x1: int, y1: int, x2: int, y2: int) -> int:
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    sum_of_s = 0
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                sum_of_s += 0  # S(x, y) is 0 for a pole itself
            else:
                min_distance_squared = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            dist = distance_squared(x, y, i, j)
                            if dist < min_distance_squared:
                                min_distance_squared = dist
                sum_of_s += min_distance_squared
    return sum_of_s

