def find_max_f(sequence: list[int]) -> int:
    def f(seq: list[int]) -> int:
        return sum(1 for i, x in enumerate(seq, 1) if i == x)

    max_f = f(sequence)
    current_sequence = sequence[:]

    while True:
        to_remove = []
        for i, x in enumerate(current_sequence, 1):
            if i != x:
                to_remove.append(x)

        if not to_remove:
            break

        for x in to_remove:
            current_sequence.remove(x)

        current_f = f(current_sequence)
        if current_f > max_f:
            max_f = current_f

    return max_f

# Interface using input() and print()
