from typing import List, Tuple
import sys

# Assuming a tree is represented as an adjacency list
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List['TreeNode'] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

class Tree:
    def __init__(self, n: int, illusion_rates: List[int]):
        self.nodes = [TreeNode(i + 1, illusion_rates[i]) for i in range(n)]
        self.root = self.nodes[0]

    def add_edge(self, s: int, t: int):
        self.nodes[s - 1].add_child(self.nodes[t - 1])
        self.nodes[t - 1].add_child(self.nodes[s - 1])

    def update_illusion_rate(self, room_id: int, new_rate: int):
        node = self._find_node(room_id)
        if node:
            node.illusion_rate = new_rate

    def _find_node(self, room_id: int) -> TreeNode:
        for node in self.nodes:
            if node.room_id == room_id:
                return node
        return None

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        start_node = self._find_node(start_room)
        end_node = self._find_node(end_room)
        if not start_node or not end_node:
            return -1  # Error: room not found

        # Use DFS to find the minimum energy path
        stack = [(start_node, 0)]
        visited = set()
        while stack:
            current_node, current_energy = stack.pop()
            if current_node.room_id == end_node.room_id:
                return current_energy + max(abs(start_node.illusion_rate + end_node.illusion_rate), abs(start_node.illusion_rate - end_node.illusion_rate))
            if current_node in visited:
                continue
            visited.add(current_node)
            for child in current_node.children:
                if child not in visited:
                    stack.append((child, current_energy + max(abs(current_node.illusion_rate + child.illusion_rate), abs(current_node.illusion_rate - child.illusion_rate))))
        return -1  # Error: no path found

# Read input from stdin
n, q = map(int, sys.stdin.readline().split())
illusion_rates = list(map(int, sys.stdin.readline().split()))
tree = Tree(n, illusion_rates)

for _ in range(n - 1):
    s, t = map(int, sys.stdin.readline().split())
    tree.add_edge(s, t)

for _ in range(q):
    query = list(map(int, sys.stdin.readline().split()))
    if query[0] == 1:
        tree.update_illusion_rate(query[1], query[2])
    elif query[0] == 2:
        result = tree.calculate_minimum_energy(query[1], query[2])
        print(result)

