from typing import List

def calculate_sum_of_squares(n: int, m: int, grid: List[List[int]]) -> int:
    def is_pole(x: int, y: int) -> bool:
        return grid[x][y] == 1

    def distance_squared(x1: int, y1: int, x2: int, y2: int) -> int:
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    # Precompute the prefix sum for poles
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]
    for x in range(1, n + 1):
        for y in range(1, m + 1):
            prefix_sum[x][y] = prefix_sum[x - 1][y] + prefix_sum[x][y - 1] - prefix_sum[x - 1][y - 1] + is_pole(x, y)

    def query_prefix_sum(x1: int, y1: int, x2: int, y2: int) -> int:
        return prefix_sum[x2][y2] - prefix_sum[x1 - 1][y2] - prefix_sum[x2][y1 - 1] + prefix_sum[x1 - 1][y1 - 1]

    sum_of_squares = 0

    for x in range(n + 1):
        for y in range(m + 1):
            if is_pole(x, y):
                sum_of_squares += 0  # Distance to self is 0
            else:
                # Use prefix sum to find the nearest pole
                low_x, high_x = 1, n
                low_y, high_y = 1, m
                while low_x < high_x or low_y < high_y:
                    mid_x = (low_x + high_x) // 2
                    mid_y = (low_y + high_y) // 2
                    if query_prefix_sum(x, y, mid_x, mid_y) == 0:
                        low_x, high_x = mid_x + 1, high_x
                        low_y, high_y = mid_y + 1, high_y
                    else:
                        high_x, high_y = mid_x, mid_y

                nearest_pole_x, nearest_pole_y = low_x, low_y
                sum_of_squares += distance_squared(x, y, nearest_pole_x, nearest_pole_y)

    return sum_of_squares

