def compare_numbers(x1, p1, x2, p2):
    # Normalize the numbers by removing trailing zeros
    while x1 % 10 == 0 and p1 > 0:
        x1 //= 10
        p1 -= 1
    while x2 % 10 == 0 and p2 > 0:
        x2 //= 10
        p2 -= 1

    # Compare the number of digits after removing trailing zeros
    if p1 > p2:
        return '>'
    elif p1 < p2:
        return '<'
    else:
        if x1 > x2:
            return '>'
        elif x1 < x2:
            return '<'
        else:
            return '='

