from typing import List, Tuple
import sys

# Assuming we are using a graph library to handle the tree operations efficiently
# We will use a simple adjacency list to represent the tree

class Room:
    def __init__(self, index: int, illusion_rate: int):
        self.index = index
        self.illusion_rate = illusion_rate

class Tree:
    def __init__(self, n: int):
        self.rooms = [Room(i, 0) for i in range(n + 1)]
        self.adj_list = [[] for _ in range(n + 1)]

    def add_edge(self, u: int, v: int):
        self.adj_list[u].append(v)
        self.adj_list[v].append(u)

    def update_illusion_rate(self, room_index: int, new_rate: int):
        self.rooms[room_index].illusion_rate = new_rate

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        # Using DFS to find the minimum energy required to travel between two rooms
        def dfs(node: int, parent: int, target: int) -> Tuple[bool, int]:
            if node == target:
                return True, 0

            min_energy = sys.maxsize
            found = False
            for neighbor in self.adj_list[node]:
                if neighbor != parent:
                    found, energy = dfs(neighbor, node, target)
                    if found:
                        min_energy = min(min_energy, energy + max(abs(self.rooms[node].illusion_rate + self.rooms[neighbor].illusion_rate), abs(self.rooms[node].illusion_rate - self.rooms[neighbor].illusion_rate)))

            return found, min_energy

        _, energy = dfs(start_room, -1, end_room)
        return energy

# Function signatures as per the given interface
def update_illusion_rate(room_index: int, new_rate: int) -> None:
    tree.update_illusion_rate(room_index, new_rate)

def calculate_minimum_energy(start_room: int, end_room: int) -> int:
    return tree.calculate_minimum_energy(start_room, end_room)

