from typing import List

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1

    # Pre-process the distances using 2D prefix sum
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if grid[i][j] == 1:
                prefix_sum[i][j] = 1

    # Compute prefix sums
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] += prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1]

    def sum_of_s(x, y):
        # Calculate the sum of S(x, y) for all points (x, y) in the grid
        def query(x1, y1, x2, y2):
            return prefix_sum[x2][y2] - prefix_sum[x1 - 1][y2] - prefix_sum[x2][y1 - 1] + prefix_sum[x1 - 1][y1 - 1]

        if grid[x][y] == 1:
            return 0

        # Find the nearest pole
        low = 1
        high = min(x, y)
        nearest_pole_distance = 0

        while low <= high:
            mid = (low + high) // 2
            if query(x - mid + 1, y - mid + 1, x, y) > 0:
                nearest_pole_distance = mid
                low = mid + 1
            else:
                high = mid - 1

        return (x + 1) ** 2 + (y + 1) ** 2 - nearest_pole_distance ** 2

    total_sum = 0
    for i in range(n + 1):
        for j in range(m + 1):
            total_sum += sum_of_s(i, j)

    return total_sum

