import math

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    s_sum = 0

    # Create a 2D array to store the minimum distance to the nearest pole
    min_distance = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    # Pre-process the grid to find the nearest pole for each point
    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                min_distance[i][j] = 0
            elif i > 0:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i - 1][j] + 1)
            if j > 0:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j - 1] + 1)

    # Fill the remaining cells
    for i in range(n + 1):
        for j in range(m + 1):
            if i < n:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i + 1][j] + 1)
            if j < m:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j + 1] + 1)

    # Calculate the sum of all S(x, y)
    for x in range(n + 1):
        for y in range(m + 1):
            s_sum += min_distance[x][y] ** 2

    return s_sum

