from collections import deque

def find_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def bfs(start_row: int, start_col: int) -> int:
        visited = [[False] * m for _ in range(n)]
        queue = deque([(start_row, start_col, 0)])  # (row, col, steps)
        visited[start_row][start_col] = True
        max_steps = 0

        while queue:
            row, col, steps = queue.popleft()
            max_steps = max(max_steps, steps)
            next_row, next_col = row, col

            if board[row][col] == 'L' and col > 0:
                next_col = col - 1
            elif board[row][col] == 'R' and col < m - 1:
                next_col = col + 1
            elif board[row][col] == 'D' and row < n - 1:
                next_row = row + 1
            elif board[row][col] == 'U' and row > 0:
                next_row = row - 1

            if not visited[next_row][next_col]:
                visited[next_row][next_col] = True
                queue.append((next_row, next_col, steps + 1))

        return max_steps

    max_steps = 0
    best_start = (0, 0, 0)

    for start_row in range(n):
        for start_col in range(m):
            steps = bfs(start_row, start_col)
            if steps > max_steps:
                max_steps = steps
                best_start = (start_row + 1, start_col + 1, steps)

    return best_start

